create table flying ( 
    flight_num varchar(20) not null,
    airplane_id varchar(20),
    airgroup varchar(20),
    start_date DATE,
    mileage int,
    start_hour int,
    start_min int,
    start_sec int,
    end_hour int,
    end_min int,
    end_sec int, 
    start_place varchar(20),
    end_place varchar(20),
    first_seat int,
    business_seat int,
    economic_seat int,
    primary key(flight_num, start_date)
)ENGINE = INNODB;

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              
                                                                                                
                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              

                                                                      
                                                          



                                                          

                              




